package ciclos;

public class Ejemplo_While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =1;
		
		// salir cuando es > que 4
		while (a <= 4) {
			System.out.println("el valor de A: " + a);
			// incrementa el valor de 'a' para la siguiente iteracion
			a++;
		}

	}

}
